import React, { useState, useEffect, useRef, useCallback } from "react";
import { Button, ListGroup, FormControl } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Watchlist.css";

const Watchlist = ({ onSelectSymbol, isCollapsed }) => {
  const [watchlist, setWatchlist] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const widgetRef = useRef(null);

  const handleWidgetMessage = useCallback((event) => {
    if (event.data && event.data.includes("search_symbol")) {
      const symbolData = JSON.parse(event.data);
      setSearchResults(symbolData.symbols);
    }
  }, []);

  useEffect(() => {
    if (!widgetRef.current) {
      const script = document.createElement("script");
      script.src =
        "https://s3.tradingview.com/external-embedding/embed-widget-symbol-search.js";
      script.async = true;
      script.innerHTML = JSON.stringify({
        width: "100%",
        height: "450",
        default_symbol: "NASDAQ:AAPL",
        default_exchange: "NASDAQ",
        symbol_search_autocomplete: true,
        show_symbol_search_input: true,
        show_exchange_filter: true,
        show_hotlist: true,
        show_ideas: true,
        locale: "en",
      });
      widgetRef.current.appendChild(script);
      window.addEventListener("message", handleWidgetMessage);
    }

    return () => {
      window.removeEventListener("message", handleWidgetMessage);
    };
  }, [handleWidgetMessage]);

  const handleAddSymbol = (symbol) => {
    if (!watchlist.includes(symbol)) {
      setWatchlist([...watchlist, symbol]);
    }
  };

  const handleRemoveSymbol = (symbol) => {
    setWatchlist(watchlist.filter((item) => item !== symbol));
  };

  return (
    <div className="watchlist">
      <h5>Watchlist</h5>
      {!isCollapsed && (
        <>
          <div ref={widgetRef} className="tradingview-widget-container"></div>
          <FormControl
            type="text"
            placeholder="Search..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <ListGroup>
            {searchResults
              .filter((symbol) => symbol.includes(searchQuery))
              .map((symbol) => (
                <ListGroup.Item key={symbol}>
                  <div className="watchlist-item">
                    <span onClick={() => onSelectSymbol(symbol)}>{symbol}</span>
                    <Button
                      variant="primary"
                      size="sm"
                      onClick={() => handleAddSymbol(symbol)}
                    >
                      Add
                    </Button>
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={() => handleRemoveSymbol(symbol)}
                    >
                      Remove
                    </Button>
                  </div>
                </ListGroup.Item>
              ))}
          </ListGroup>
        </>
      )}
    </div>
  );
};

export default Watchlist;
