// frontend/src/App.js

import React, { useState, useEffect } from "react";
import { fetchStocks, fetchStockPrice } from "./api";

function App() {
  const [stocks, setStocks] = useState([]);
  const [stockPrices, setStockPrices] = useState({});

  useEffect(() => {
    fetchStocks()
      .then((response) => {
        setStocks(response.data);
        response.data.forEach((stock) => {
          fetchStockPrice(stock.symbol)
            .then((priceResponse) => {
              const timeSeries = priceResponse.data["Time Series (5min)"];
              const latestTime = Object.keys(timeSeries)[0];
              const latestPrice = timeSeries[latestTime]["1. open"];
              setStockPrices((prevState) => ({
                ...prevState,
                [stock.symbol]: latestPrice,
              }));
            })
            .catch((error) => {
              console.error(
                "There was an error fetching the stock price!",
                error
              );
            });
        });
      })
      .catch((error) => {
        console.error("There was an error fetching the stocks!", error);
      });
  }, []);

  return (
    <div className="App">
      <h1>Stock Trading Demo</h1>
      <h2>Stock List</h2>
      <ul>
        {stocks.map((stock) => (
          <li key={stock.id}>
            {stock.name} ({stock.symbol}) - Price:{" "}
            {stockPrices[stock.symbol] || "Loading..."}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
