// src/components/TradeForm.js
import React, { useState } from "react";
import "./TradeForm.css"; // לוודא שהקובץ הזה קיים וטעון נכון

const TradeForm = ({ currentSymbol, currentPrice }) => {
  const [quantity, setQuantity] = useState(0);
  const [action, setAction] = useState("buy");

  const handleSubmit = (event) => {
    event.preventDefault();
    const totalAmount = quantity * currentPrice;

    // כאן ניתן להוסיף את הקוד לשליחת פקודת קנייה או מכירה לשרת
    console.log(
      `Symbol: ${currentSymbol}, Action: ${action}, Quantity: ${quantity}, Total Amount: ${totalAmount}`
    );
  };

  return (
    <div className="trade-form-container">
      <h2>Trade Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Symbol:</label>
          <span>{currentSymbol}</span>
        </div>
        <div>
          <label>Price:</label>
          <span>${currentPrice.toFixed(2)}</span>
        </div>
        <div>
          <label>Quantity:</label>
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            min="1"
            required
          />
        </div>
        <div>
          <label>Action:</label>
          <select value={action} onChange={(e) => setAction(e.target.value)}>
            <option value="buy">Buy</option>
            <option value="sell">Sell</option>
          </select>
        </div>
        <button type="submit">Submit Order</button>
      </form>
    </div>
  );
};

export default TradeForm;
