import React, { useEffect, useRef } from "react";
import "./AdvancedTradingViewChart.css";

const AdvancedTradingViewChart = ({
  theme,
  setCurrentSymbol,
  setCurrentPrice,
  currentSymbol,
}) => {
  const chartContainerRef = useRef(null);

  useEffect(() => {
    const widget = new window.TradingView.widget({
      container_id: chartContainerRef.current,
      symbol: currentSymbol,
      interval: "D",
      theme: theme === "dark" ? "Dark" : "Light",
      style: "1",
      locale: "en",
      toolbar_bg: theme === "dark" ? "#1f1f1f" : "#f1f3f6",
      enable_publishing: false,
      allow_symbol_change: true,
      save_image: false,
      details: true,
      hotlist: true,
      calendar: true,
      studies: ["MACD@tv-basicstudies"],
      withdateranges: true,
      hide_side_toolbar: false,
    });

    return () => {
      if (widget) {
        widget.remove();
      }
    };
  }, [theme, currentSymbol]);

  return (
    <div ref={chartContainerRef} className="tradingview-widget-container" />
  );
};

export default AdvancedTradingViewChart;
