// BitcoinChart.js
import React, { useEffect, useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import { getChartOptions } from "./chartOptions";
import "./BitcoinChart.css"; // נייבא את קובץ ה-CSS המתאים

const BitcoinChart = () => {
  const [chartOptions, setChartOptions] = useState({});
  const [chartRef, setChartRef] = useState(null);
  const [chartType, setChartType] = useState("candlestick"); // מצב התחלתי של סוג גרף

  useEffect(() => {
    const fetchBitcoinData = async () => {
      const data = await fetch(
        "https://demo-live-data.highcharts.com/aapl-ohlcv.json"
      ).then((response) => response.json());

      const ohlc = [],
        volume = [],
        dataLength = data.length;

      for (let i = 0; i < dataLength; i += 1) {
        ohlc.push([
          data[i][0], // the date
          data[i][1], // open
          data[i][2], // high
          data[i][3], // low
          data[i][4], // close
        ]);

        volume.push([
          data[i][0], // the date
          data[i][5], // the volume
        ]);
      }

      setChartOptions(getChartOptions(ohlc, volume, chartType));
    };

    fetchBitcoinData();
  }, [chartType]);

  useEffect(() => {
    if (chartRef) {
      const handleResize = () => {
        if (chartRef) {
          chartRef.reflow(); // עדכון גובה ורוחב הגרף בהתאם לשינוי גובה
        }
      };

      window.addEventListener("resize", handleResize);

      return () => {
        window.removeEventListener("resize", handleResize);
      };
    }
  }, [chartRef]);

  const toggleChartType = () => {
    setChartType((prevType) =>
      prevType === "candlestick" ? "line" : "candlestick"
    );
  };

  return (
    <div className="chart-container">
      <button className="chart-type-toggle" onClick={toggleChartType}>
        {chartType === "candlestick"
          ? "Switch to Line Chart"
          : "Switch to Candlestick Chart"}
      </button>
      <HighchartsReact
        highcharts={Highcharts}
        constructorType={"stockChart"}
        options={chartOptions}
        ref={(chart) => setChartRef(chart?.chart)}
      />
    </div>
  );
};

export default BitcoinChart;
