import React, { useEffect } from "react";

const TradingViewChart = () => {
  useEffect(() => {
    new window.TradingView.widget({
      autosize: true,
      symbol: "NASDAQ:AAPL",
      interval: "D",
      timezone: "Etc/UTC",
      theme: "dark",
      style: 1, // 1 for Candlestick, 0 for OHLC
      locale: "en",
      toolbar_bg: "#f1f3f6",
      enable_publishing: true, // Enable the feature to save and manage chart layouts
      allow_symbol_change: true,
      container_id: "tradingview_chart",
      studies: [
        "BB@tv-basicstudies", // Bollinger Bands
        "MAExp@tv-basicstudies", // Exponential Moving Average
        "RSI@tv-basicstudies", // Relative Strength Index
        "MACD@tv-basicstudies", // Moving Average Convergence Divergence
        "SMA@tv-basicstudies", // Simple Moving Average
      ],
      hide_side_toolbar: false, // Make sure the side toolbar is visible
      hide_top_toolbar: false,
      save_image: true, // Enable saving the chart image
      popup: {
        enabled: true,
        // Enable and configure the popup for saving and managing chart layouts
        saveLayout: {
          enabled: true, // Enable saving layouts
        },
        layoutPopup: {
          enabled: true, // Show the layout management popup
        },
      },
      studies_overrides: {
        "bb.topLine.color": "#ff0000",
        "bb.middleLine.color": "#00ff00",
        "bb.bottomLine.color": "#0000ff",
        "sma.color": "#ff6600",
        "rsi.color": "#33ccff",
      },
      overrides: {
        "paneProperties.background": "#000000",
        "paneProperties.vertGridColor": "#4b4b4b",
        "paneProperties.horzGridColor": "#4b4b4b",
        "symbolWatermarkProperties.color": "rgba(255, 255, 255, 0.2)",
        "scalesProperties.textColor": "#d0d0d0",
        "mainSeriesProperties.candleStyle.upColor": "#4fff5b",
        "mainSeriesProperties.candleStyle.borderUpColor": "#4fff5b",
        "mainSeriesProperties.candleStyle.wickUpColor": "#4fff5b",
        "mainSeriesProperties.candleStyle.downColor": "#ff4f4f",
        "mainSeriesProperties.candleStyle.borderDownColor": "#ff4f4f",
        "mainSeriesProperties.candleStyle.wickDownColor": "#ff4f4f",
      },
      custom_css_url: "https://example.com/custom.css", // מקום להוסיף קובץ CSS מותאם אישית
    });
  }, []);

  return (
    <div className="tradingview-widget-container">
      <div id="tradingview_chart" style={{ height: "600px" }}></div>
    </div>
  );
};

export default TradingViewChart;
