import React, { useState } from "react";
import { Button, Form, ListGroup } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Watchlist.css";

const Watchlist = ({ onSelectSymbol }) => {
  const [watchlist, setWatchlist] = useState([]);
  const [newSymbol, setNewSymbol] = useState("");

  const handleAddSymbol = () => {
    if (newSymbol && !watchlist.includes(newSymbol)) {
      setWatchlist([...watchlist, newSymbol]);
      setNewSymbol("");
    }
  };

  const handleRemoveSymbol = (symbol) => {
    setWatchlist(watchlist.filter((item) => item !== symbol));
  };

  return (
    <div className="watchlist">
      <h5>Watchlist</h5>
      <Form
        onSubmit={(e) => {
          e.preventDefault();
          handleAddSymbol();
        }}
      >
        <Form.Group controlId="formNewSymbol">
          <Form.Control
            type="text"
            placeholder="Add new symbol"
            value={newSymbol}
            onChange={(e) => setNewSymbol(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Add
        </Button>
      </Form>
      <ListGroup>
        {watchlist.map((symbol) => (
          <ListGroup.Item key={symbol}>
            <div className="watchlist-item">
              <span onClick={() => onSelectSymbol(symbol)}>{symbol}</span>
              <Button
                variant="danger"
                size="sm"
                onClick={() => handleRemoveSymbol(symbol)}
              >
                Remove
              </Button>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
};

export default Watchlist;
