// chartOptions.js
export const getChartOptions = (ohlc, volume) => ({
  chart: {
    backgroundColor: "#353535", // צבע רקע כהה
    style: {
      fontFamily: "Arial, sans-serif",
    },
    events: {
      load: function () {
        addGUIEvents(this);
      },
    },
  },
  title: {
    text: "Bitcoin Price Chart",
    style: {
      color: "#ffffff", // צבע טקסט כותרת לבן
    },
  },
  xAxis: {
    gridLineWidth: 0, // הסרת קווי הרשת בציר ה-X
    crosshair: {
      enabled: false, // הסרת הקווים המאוזנים בציר ה-X
    },
    labels: {
      style: {
        color: "#ffffff", // צבע תוויות לבן
      },
    },
  },
  yAxis: [
    {
      title: {
        text: "Price (USD)",
        style: {
          color: "#ffffff", // צבע טקסט כותרת ציר ימין לבן
        },
      },
      gridLineWidth: 0, // הסרת קווי הרשת בציר ה-Y
      crosshair: {
        enabled: false, // הסרת הקווים המאוזנים בציר ה-Y
      },
      labels: {
        style: {
          color: "#ffffff", // צבע תוויות ציר ימין לבן
        },
      },
      height: "70%",
    },
    {
      title: {
        text: "Volume",
        style: {
          color: "#ffffff", // צבע טקסט כותרת ציר ימין לבן
        },
      },
      gridLineWidth: 0, // הסרת קווי הרשת בציר ה-Y של ה-volume
      crosshair: {
        enabled: false, // הסרת הקווים המאוזנים בציר ה-Y של ה-volume
      },
      labels: {
        style: {
          color: "#ffffff", // צבע תוויות ציר ימין לבן
        },
      },
      top: "70%",
      height: "30%",
      offset: 0,
    },
  ],
  tooltip: {
    shape: "square",
    split: false,
    shared: true,
    headerShape: "callout",
    shadow: false,
    backgroundColor: "#333333", // צבע רקע חלון מידע כהה
    borderColor: "#ffffff", // צבע גבול חלון מידע לבן
    style: {
      color: "#ffffff", // צבע טקסט חלון מידע לבן
    },
  },
  series: [
    {
      type: "candlestick",
      id: "aapl-ohlc",
      name: "Bitcoin Price",
      upColor: "#00ff00", // ירוק עבור עליות
      color: "#ff0000", // אדום עבור ירידות
      data: ohlc,
    },
    {
      type: "column",
      id: "aapl-volume",
      name: "Volume",
      data: volume,
      yAxis: 1,
    },
  ],
  rangeSelector: {
    selected: 1, // ברירת מחדל ל-3 חודשים
    buttons: [
      {
        type: "month",
        count: 1,
        text: "1m",
      },
      {
        type: "month",
        count: 3,
        text: "3m",
      },
      {
        type: "month",
        count: 6,
        text: "6m",
      },
      {
        type: "ytd",
        text: "YTD",
      },
      {
        type: "year",
        count: 1,
        text: "1y",
      },
      {
        type: "all",
        text: "All",
      },
    ],
    buttonTheme: {
      fill: "#333333", // צבע רקע כפתורים
      stroke: "#ffffff", // צבע גבול כפתורים
      style: {
        color: "#a1a1a1", // צבע טקסט בכפתורים
      },
      states: {
        hover: {
          fill: "#555555", // צבע רקע בזמן ריחוף מעל כפתורים
          stroke: "#ffffff", // צבע גבול בזמן ריחוף מעל כפתורים
        },
        select: {
          fill: "#444444", // צבע רקע כאשר נבחר כפתור
          stroke: "#ffffff", // צבע גבול כאשר נבחר כפתור
        },
      },
    },
  },
  stockTools: {
    gui: {
      enabled: true,
      icons: {
        indicator: "highcharts-icon-indicator",
        annotation: "highcharts-icon-annotation",
        zoomIn: "highcharts-icon-zoom-in",
        zoomOut: "highcharts-icon-zoom-out",
        pan: "highcharts-icon-pan",
        resetZoom: "highcharts-icon-reset-zoom",
      },
      style: {
        color: "#ffffff",
        backgroundColor: "#444444",
        borderColor: "#666666",
      },
    },
    buttons: {
      indicators: {
        text: "Indicators",
      },
      annotations: {
        text: "Annotations",
      },
    },
  },
  responsive: {
    rules: [
      {
        condition: {
          maxWidth: 800,
        },
        chartOptions: {
          rangeSelector: {
            inputEnabled: false,
          },
        },
      },
    ],
  },
});

function addGUIEvents(chart) {
  Highcharts.addEvent(
    document.querySelectorAll(".highcharts-close-popup")[0],
    "click",
    function () {
      this.parentNode.style.display = "none";
    }
  );

  Highcharts.addEvent(
    document.querySelectorAll(".highcharts-popup-indicators button")[0],
    "click",
    function () {
      const typeSelect = document.querySelectorAll(
          ".highcharts-popup-indicators select"
        )[0],
        type = typeSelect.options[typeSelect.selectedIndex].value,
        period =
          document.querySelectorAll(".highcharts-popup-indicators input")[0]
            .value || 14;

      chart.addSeries({
        linkedTo: "aapl-ohlc",
        type: type,
        params: {
          period: parseInt(period, 10),
        },
      });

      chart.stockToolbar.indicatorsPopupContainer.style.display = "none";
    }
  );

  Highcharts.addEvent(
    document.querySelectorAll(".highcharts-popup-annotations " + "button")[0],
    "click",
    function () {
      const strokeWidth = parseInt(
          document.querySelectorAll(
            ".highcharts-popup-annotations " + 'input[name="stroke-width"]'
          )[0].value,
          10
        ),
        strokeColor = document.querySelectorAll(
          '.highcharts-popup-annotations input[name="stroke"]'
        )[0].value;

      if (chart.currentAnnotation.options.typeOptions) {
        chart.currentAnnotation.update({
          typeOptions: {
            lineColor: strokeColor,
            lineWidth: strokeWidth,
            line: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
            background: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
            innerBackground: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
            outerBackground: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
            connector: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
          },
        });
      } else {
        chart.currentAnnotation.update({
          shapes: [
            {
              "stroke-width": strokeWidth,
              stroke: strokeColor,
            },
          ],
          labels: [
            {
              borderWidth: strokeWidth,
              borderColor: strokeColor,
            },
          ],
        });
      }
      chart.stockToolbar.annotationsPopupContainer.style.display = "none";
    }
  );
}
