import React, { useEffect } from "react";

const AdvancedTradingViewChart = ({ onSymbolChange, onPriceChange }) => {
  useEffect(() => {
    new window.TradingView.widget({
      autosize: true,
      symbol: "NASDAQ:AAPL",
      interval: "D",
      timezone: "Etc/UTC",
      theme: "light",
      style: "1",
      locale: "en",
      toolbar_bg: "#f1f3f6",
      enable_publishing: false,
      allow_symbol_change: true,
      container_id: "tradingview_advanced_chart",
      studies: ["MAExp@tv-basicstudies", "RSI@tv-basicstudies"],
      withdateranges: true,
      hide_side_toolbar: false,
      hide_top_toolbar: false,
      save_image: false,
      studies_overrides: {},
      overrides: {},
      onSymbolChange: (symbolInfo) => {
        const { ticker, price } = symbolInfo;
        onSymbolChange(ticker);
        onPriceChange(price);
      },
    });

    // No need for cleanup here as we are not saving the widget instance
  }, [onSymbolChange, onPriceChange]);

  return (
    <div className="tradingview-widget-container">
      <div id="tradingview_advanced_chart" style={{ height: "600px" }}></div>
    </div>
  );
};

export default AdvancedTradingViewChart;
