// CZ8X40Q1W69GQGKB
import React, { useEffect, useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import axios from "axios";

const BitcoinChart = () => {
  const [chartOptions, setChartOptions] = useState({
    title: {
      text: "Bitcoin Price Chart",
    },
    xAxis: {
      crosshair: {
        className: "highcharts-crosshair-custom",
        enabled: true,
      },
      type: "datetime",
    },
    yAxis: [
      {
        title: {
          text: "Price (USD)",
        },
        crosshair: {
          snap: false,
          className: "highcharts-crosshair-custom",
          label: {
            className: "highcharts-crosshair-custom-label",
            enabled: true,
            format: "{value:.2f}",
          },
          enabled: true,
        },
        labels: {
          align: "left",
        },
        height: "70%",
      },
    ],
    tooltip: {
      shared: true,
      headerShape: "callout",
      shadow: false,
      pointFormatter: function () {
        return `<span style="font-size: 1.4em">${this.series.name}</span><br/>
                <span style="color: #ff6e6e;">Open: ${this.open}</span><br/>
                <span style="color: #ff6e6e;">High: ${this.high}</span><br/>
                <span style="color: #ff6e6e;">Low: ${this.low}</span><br/>
                <span style="color: #ff6e6e;">Close: ${this.close}</span><br/>
                Volume: ${this.volume}`;
      },
    },
    series: [
      {
        type: "candlestick",
        name: "Bitcoin Price",
        data: [], // נתונים יתעדכנו בהמשך
      },
    ],
    rangeSelector: {
      verticalAlign: "bottom",
    },
    responsive: {
      rules: [
        {
          condition: {
            maxWidth: 800,
          },
          chartOptions: {
            rangeSelector: {
              inputEnabled: false,
            },
          },
        },
      ],
    },
  });

  useEffect(() => {
    const fetchBitcoinData = async () => {
      try {
        const response = await axios.get("https://www.alphavantage.co/query", {
          params: {
            function: "DIGITAL_CURRENCY_INTRADAY",
            symbol: "BTC",
            market: "USD",
            interval: "1min", // הוסף פרמטר זה כדי להבטיח שאתה מקבל נתונים לכל דקה
            apikey: "CZ8X40Q1W69GQGKB", // החלף ב-API Key שלך
          },
        });

        console.log(response.data); // הוסף את זה כדי לראות את התגובה מה-API

        const data = response.data["Time Series (Digital Currency Intraday)"];
        if (!data) {
          throw new Error("No data received from Alpha Vantage");
        }

        const ohlc = [];
        for (const [time, info] of Object.entries(data)) {
          ohlc.push([
            new Date(time).getTime(), // the date
            parseFloat(info["1a. price (USD)"]), // open
            parseFloat(info["2a. high (USD)"]), // high
            parseFloat(info["3a. low (USD)"]), // low
            parseFloat(info["4a. close (USD)"]), // close
          ]);
        }

        setChartOptions((prevOptions) => ({
          ...prevOptions,
          xAxis: {
            ...prevOptions.xAxis,
            min: ohlc[0][0],
            max: ohlc[ohlc.length - 1][0],
          },
          series: [
            {
              ...prevOptions.series[0],
              data: ohlc,
            },
          ],
        }));
      } catch (error) {
        console.error("Error fetching Bitcoin data:", error);
      }
    };

    fetchBitcoinData();
    const interval = setInterval(fetchBitcoinData, 60000); // עדכון כל דקה
    return () => clearInterval(interval);
  }, []);

  return (
    <div id="container">
      <HighchartsReact
        highcharts={Highcharts}
        constructorType={"stockChart"}
        options={chartOptions}
      />
    </div>
  );
};

export default BitcoinChart;
