import React, { useState } from "react";
import "./index.css";
import "./App.css"; // ייבוא קובץ ה-CSS של רכיב ה-App
import MyNavbar from "./components/MyNavbar"; // ייבוא ה-Navbar המתקדם שלך
import AdvancedTradingViewChart from "./components/AdvancedTradingViewChart";
import TradeForm from "./components/TradeForm"; // ייבוא הקומפוננט של קנייה ומכירה

const App = () => {
  const [currentSymbol, setCurrentSymbol] = useState("NASDAQ:AAPL"); // המניה הנוכחית
  const [currentPrice, setCurrentPrice] = useState(150.0); // המחיר הנוכחי של המניה

  return (
    <div>
      <MyNavbar />
      <h1>TradingView Chart</h1>
      <AdvancedTradingViewChart
        onSymbolChange={setCurrentSymbol}
        onPriceChange={setCurrentPrice}
      />
      <TradeForm currentSymbol={currentSymbol} currentPrice={currentPrice} />{" "}
      {/* הוספת הקומפוננט של קנייה ומכירה מתחת לגרף */}
    </div>
  );
};

export default App;
