import React, { useEffect, useRef, useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import IndicatorsAll from "highcharts/indicators/indicators-all";
import { getChartOptions } from "./chartOptions";
import "./BitcoinChart.css";
import ChartToolbar from "./ChartToolbar";

// Initialize the required modules
IndicatorsAll(Highcharts);

const BitcoinChart = () => {
  const chartComponent = useRef(null);
  const [chartOptions, setChartOptions] = useState({});
  const [chartInstance, setChartInstance] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const data = await fetch(
        "https://demo-live-data.highcharts.com/aapl-ohlcv.json"
      ).then((response) => response.json());

      const ohlc = [];
      const volume = [];
      const dataLength = data.length;

      for (let i = 0; i < dataLength; i += 1) {
        ohlc.push([data[i][0], data[i][1], data[i][2], data[i][3], data[i][4]]);
        volume.push([
          data[i][0],
          data[i][5],
          data[i][1] < data[i][4]
            ? "highcharts-point-up"
            : "highcharts-point-down",
        ]);
      }

      const options = {
        lang: {
          accessibility: {
            defaultChartTitle: "AAPL Stock Price",
          },
        },
        xAxis: {
          crosshair: {
            className: "highcharts-crosshair-custom",
            enabled: true,
          },
        },
        yAxis: [
          {
            title: {
              text: "price (USD)",
            },
            crosshair: {
              snap: false,
              className: "highcharts-crosshair-custom",
              label: {
                className: "highcharts-crosshair-custom-label",
                enabled: true,
                format: "{value:.2f}",
              },
              enabled: true,
            },
            labels: {
              align: "left",
            },
            height: "70%",
          },
          {
            title: {
              text: "volume",
            },
            crosshair: {
              className: "highcharts-crosshair-custom",
              snap: false,
              enabled: true,
              label: {
                format:
                  "{#if value ge 1000000} {(divide value 1000000):.2f} M {else} {value} {/if}",
                className: "highcharts-crosshair-custom-label",
                enabled: true,
              },
            },
            labels: {
              align: "left",
            },
            top: "70%",
            height: "30%",
            offset: 0,
          },
        ],
        chart: {
          styledMode: true,
          events: {
            load: function () {
              addPopupEvents(this);
            },
          },
        },
        tooltip: {
          shape: "square",
          split: false,
          shared: true,
          headerShape: "callout",
          shadow: false,
          format: `<span style="font-size: 1.4em">{point.series.name}</span>
O<span style="color:{point.color}">{point.open}</span>
H<span style="color:{point.color}">{point.high}</span>
L<span style="color:{point.color}">{point.low}</span>
C<span style="color:{point.color}">{point.close}
{(subtract point.open point.close):.2f}
{(multiply (divide (subtract point.open point.close) point.close) 100):.2f}%
</span>
<br>
Volume<span style="color:{point.color}">{points.1.y}</span>`,
          positioner: () => ({ x: 60, y: 0 }),
        },
        series: [
          {
            type: "candlestick",
            id: "aapl-ohlc",
            name: "AAPL Stock Price",
            lastPrice: {
              enabled: true,
              label: {
                enabled: true,
                align: "left",
                x: 8,
              },
            },
            data: ohlc,
          },
          {
            type: "column",
            lastPrice: {
              enabled: true,
              label: {
                format:
                  "{#if value ge 1000000} {(divide value 1000000):.2f} M {else} {value} {/if}",
                enabled: true,
                align: "left",
                x: 8,
              },
            },
            keys: ["x", "y", "className"],
            id: "aapl-volume",
            name: "AAPL Volume",
            data: volume,
            yAxis: 1,
          },
        ],
        rangeSelector: {
          verticalAlign: "bottom",
        },
        responsive: {
          rules: [
            {
              condition: {
                maxWidth: 800,
              },
              chartOptions: {
                rangeSelector: {
                  inputEnabled: false,
                },
              },
            },
          ],
        },
      };

      setChartOptions(options);
    };

    fetchData();
  }, []);

  const handleChartTypeChange = (type) => {
    const newOptions = { ...chartOptions };
    newOptions.series[0].type = type;
    setChartOptions(newOptions);
  };

  const handleAddTrendline = () => {
    const newOptions = { ...chartOptions };
    newOptions.series.push({
      type: "line",
      name: "Trendline",
      data: chartOptions.series[0].data.map((point, index) => {
        const a = 0.0001;
        const b = chartOptions.series[0].data[0][4];
        return [point[0], a * index + b];
      }),
      color: "red",
      dashStyle: "Dash",
    });
    setChartOptions(newOptions);
  };

  const handleShowIndicators = () => {
    const newOptions = { ...chartOptions };
    newOptions.series.push({
      type: "sma",
      linkedTo: "aapl-ohlc",
      name: "SMA",
      params: {
        period: 14,
      },
      color: "blue",
    });
    setChartOptions(newOptions);
  };

  const handleHideIndicators = () => {
    const newOptions = { ...chartOptions };
    newOptions.series = newOptions.series.filter(
      (series) => series.type !== "sma"
    );
    setChartOptions(newOptions);
  };

  return (
    <div className="chart-container">
      <ChartToolbar
        onChartTypeChange={handleChartTypeChange}
        onAddTrendline={handleAddTrendline}
        onShowIndicators={handleShowIndicators}
        onHideIndicators={handleHideIndicators}
      />
      <HighchartsReact
        highcharts={Highcharts}
        constructorType={"stockChart"}
        options={chartOptions}
        ref={chartComponent}
        callback={(chart) => setChartInstance(chart)}
      />
    </div>
  );
};

export default BitcoinChart;

async function addPopupEvents(chart) {
  const closePopupButtons = document.getElementsByClassName(
    "highcharts-close-popup"
  );

  Highcharts.addEvent(closePopupButtons[0], "click", function () {
    this.parentNode.style.display = "none";
  });

  Highcharts.addEvent(closePopupButtons[1], "click", function () {
    this.parentNode.style.display = "none";
  });

  Highcharts.addEvent(
    document.querySelectorAll(".highcharts-popup-indicators button")[0],
    "click",
    function () {
      const typeSelect = document.querySelectorAll(
          ".highcharts-popup-indicators select"
        )[0],
        type = typeSelect.options[typeSelect.selectedIndex].value,
        period =
          document.querySelectorAll(".highcharts-popup-indicators input")[0]
            .value || 14;

      chart.addSeries({
        linkedTo: "aapl-ohlc",
        type: type,
        params: {
          period: parseInt(period, 10),
        },
      });

      chart.stockToolbar.indicatorsPopupContainer.style.display = "none";
    }
  );

  Highcharts.addEvent(
    document.querySelectorAll(".highcharts-popup-annotations button")[0],
    "click",
    function () {
      const strokeWidth = parseInt(
          document.querySelectorAll(
            '.highcharts-popup-annotations input[name="stroke-width"]'
          )[0].value,
          10
        ),
        strokeColor = document.querySelectorAll(
          '.highcharts-popup-annotations input[name="stroke"]'
        )[0].value;

      if (chart.currentAnnotation.options.typeOptions) {
        chart.currentAnnotation.update({
          typeOptions: {
            lineColor: strokeColor,
            lineWidth: strokeWidth,
            line: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
            background: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
            innerBackground: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
            outerBackground: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
            connector: {
              strokeWidth: strokeWidth,
              stroke: strokeColor,
            },
          },
        });
      } else {
        chart.currentAnnotation.update({
          shapes: [
            {
              "stroke-width": strokeWidth,
              stroke: strokeColor,
            },
          ],
          labels: [
            {
              borderWidth: strokeWidth,
              borderColor: strokeColor,
            },
          ],
        });
      }

      chart.stockToolbar.annotationsPopupContainer.style.display = "none";
    }
  );
}
