import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "./index.css";
import "./App.css"; // ייבוא קובץ ה-CSS של רכיב ה-App
import MyNavbar from "./components/MyNavbar"; // ייבוא ה-Navbar המתקדם שלך
import AdvancedTradingViewChart from "./components/AdvancedTradingViewChart";
import TradeForm from "./components/TradeForm"; // ייבוא הקומפוננט של קנייה ומכירה
import Dashboard from "./components/Dashboard"; // ייבוא הקומפוננט של דשבורד
import LoginForm from "./components/LoginForm"; // ייבוא קומפוננט ה-Login
import RegisterForm from "./components/RegisterForm"; // ייבוא קומפוננט ה-Register

const App = () => {
  const [currentSymbol, setCurrentSymbol] = useState("NASDAQ:AAPL"); // תוכל לעדכן לפי הצורך
  const [currentPrice, setCurrentPrice] = useState(150.0); // מחיר עכשווי של המניה
  const [theme, setTheme] = useState("light");

  const handleToggleTheme = (newTheme) => {
    setTheme(newTheme);
  };

  return (
    <Router>
      <MyNavbar onToggleTheme={handleToggleTheme} />
      <Routes>
        <Route
          path="/stocks"
          element={
            <>
              <AdvancedTradingViewChart
                setCurrentSymbol={setCurrentSymbol}
                setCurrentPrice={setCurrentPrice}
                theme={theme}
              />
              <TradeForm
                currentSymbol={currentSymbol}
                currentPrice={currentPrice}
              />
              <Dashboard />
            </>
          }
        />
        <Route path="/login" element={<LoginForm />} />
        <Route path="/register" element={<RegisterForm />} />
      </Routes>
    </Router>
  );
};

export default App;
