import React, { useEffect, useState } from "react";

const AdvancedTradingViewChart = ({ onSymbolChange, onPriceChange }) => {
  useEffect(() => {
    // חיבור ל-WebSocket של Alpha Vantage לקבלת מחירי מניות
    const ws = new WebSocket(
      `wss://your-websocket-url?apikey=CZ8X40Q1W69GQGKB`
    );

    ws.onopen = () => {
      console.log("WebSocket connected");
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data && data.price) {
        onPriceChange(data.price); // עדכון המחיר הנוכחי
      }
    };

    ws.onerror = (error) => {
      console.error("WebSocket error:", error);
    };

    ws.onclose = () => {
      console.log("WebSocket disconnected");
    };

    // סגירת ה-WebSocket כשהקומפוננט לא נמצא בשימוש
    return () => {
      ws.close();
    };
  }, [onPriceChange]);

  useEffect(() => {
    new window.TradingView.widget({
      autosize: true,
      symbol: "NASDAQ:AAPL",
      interval: "D",
      timezone: "Etc/UTC",
      theme: "light",
      style: "1",
      locale: "en",
      toolbar_bg: "#f1f3f6",
      enable_publishing: false,
      allow_symbol_change: true,
      container_id: "tradingview_advanced_chart",
      studies: ["MAExp@tv-basicstudies", "RSI@tv-basicstudies"],
      withdateranges: true,
      hide_side_toolbar: false,
      hide_top_toolbar: false,
      save_image: false,
      studies_overrides: {},
      overrides: {},
      // עדכון הסימבול והמחיר בעת שינוי הסימבול
      onSymbolChange: (symbolInfo) => {
        const { ticker } = symbolInfo;
        onSymbolChange(ticker); // עדכון הסימבול הנוכחי
        fetch(`https://api.tradingview.com/symbols/${ticker}/price`)
          .then((response) => response.json())
          .then((data) => onPriceChange(data.price)); // עדכון המחיר הנוכחי
      },
    });
  }, [onSymbolChange, onPriceChange]);

  return (
    <div className="tradingview-widget-container">
      <div id="tradingview_advanced_chart" style={{ height: "800px" }}></div>
    </div>
  );
};

export default AdvancedTradingViewChart;
