// frontend/src/components/StockList.js

import React, { useEffect, useState } from "react";
import axios from "axios";

const StockList = ({ onSelectStock }) => {
  const [stocks, setStocks] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8000/api/stocks/")
      .then((response) => {
        setStocks(response.data);
      })
      .catch((error) => {
        console.error("There was an error fetching the stocks!", error);
      });
  }, []);

  return (
    <div>
      <h1>Stock List</h1>
      <ul>
        {stocks.map((stock) => (
          <li key={stock.id} onClick={() => onSelectStock(stock)}>
            {stock.symbol} - {stock.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StockList;
