// frontend/src/App.js

import React, { useState, useEffect } from "react";
import StockList from "./components/StockList";
import TransactionForm from "./components/TransactionForm";
import StockChart from "./components/StockChart";
import axios from "axios";

const App = () => {
  const [selectedStock, setSelectedStock] = useState(null);
  const [chartData, setChartData] = useState([]);

  const fetchStockData = (stock) => {
    axios
      .get(`http://localhost:8000/api/transactions/?stock=${stock.id}`)
      .then((response) => {
        // התאם את הפורמט של הנתונים בהתאם לתוצאה של ה-API
        const data = response.data.map((tx) => ({
          date: tx.date.split("T")[0],
          price: tx.price_per_stock,
        }));
        setChartData(data);
      })
      .catch((error) => {
        console.error("There was an error fetching the stock data!", error);
      });
  };

  useEffect(() => {
    if (selectedStock) {
      fetchStockData(selectedStock);
    }
  }, [selectedStock]);

  return (
    <div>
      <StockList onSelectStock={setSelectedStock} />
      {selectedStock && (
        <>
          <TransactionForm
            stock={selectedStock}
            onSuccess={() => fetchStockData(selectedStock)}
          />
          <StockChart data={chartData} />
        </>
      )}
    </div>
  );
};

export default App;
