// chartOptions.js
export const getChartOptions = (ohlc, volume, chartType) => ({
  chart: {
    backgroundColor: "#353535", // שחור רקע
    style: {
      fontFamily: "Arial, sans-serif",
    },
  },
  title: {
    text: "Bitcoin Price Chart",
    style: {
      color: "#ffffff", // לבן כותרת
    },
  },
  xAxis: {
    gridLineWidth: 0, // הסתרת קווי הרשת בציר ה-X
    crosshair: {
      enabled: false, // הסתרת הקווים המאוזנים בציר ה-X
    },
    labels: {
      style: {
        color: "#ffffff", // לבן תוויות
      },
    },
  },
  yAxis: [
    {
      title: {
        text: "Price (USD)",
        style: {
          color: "#ffffff", // לבן כותרת ציר
        },
      },
      gridLineWidth: 0, // הסתרת קווי הרשת בציר ה-Y
      crosshair: {
        enabled: false, // הסתרת הקווים המאוזנים בציר ה-Y
      },
      labels: {
        style: {
          color: "#ffffff", // לבן תוויות
        },
      },
      height: "90%",
    },
    {
      title: {
        text: "Volume",
        style: {
          color: "#ffffff", // לבן כותרת ציר
        },
      },
      gridLineWidth: 0, // הסתרת קווי הרשת בציר ה-Y של ה-volume
      crosshair: {
        enabled: false, // הסתרת הקווים המאוזנים בציר ה-Y של ה-volume
      },
      labels: {
        style: {
          color: "#ffffff", // לבן תוויות
        },
      },
      top: "10%",
      height: "30%",
      offset: 0,
    },
  ],
  tooltip: {
    shape: "square",
    split: false,
    shared: true,
    headerShape: "callout",
    shadow: false,
    backgroundColor: "#333333", // שחור רקע חלון מידע
    borderColor: "#ffffff", // לבן גבול חלון מידע
    style: {
      color: "#ffffff", // לבן טקסט חלון מידע
    },
  },
  series: [
    {
      type: chartType, // סוג הגרף בהתאם למצב הנוכחי
      id: "aapl-ohlc",
      name: "AAPL Stock Price",
      upColor: "#00ff00", // ירוק עליות
      color: "#ff0000", // אדום ירידות
      data: ohlc,
    },
    {
      type: "column",
      id: "aapl-volume",
      name: "AAPL Volume",
      data: volume,
      yAxis: 1,
    },
  ],
  rangeSelector: {
    selected: 1, // Default to 1 month
    buttons: [
      {
        type: "month",
        count: 1,
        text: "1m",
      },
      {
        type: "month",
        count: 3,
        text: "3m",
      },
      {
        type: "month",
        count: 6,
        text: "6m",
      },
      {
        type: "ytd",
        text: "YTD",
      },
      {
        type: "year",
        count: 1,
        text: "1y",
      },
      {
        type: "all",
        text: "All",
      },
    ],
    buttonTheme: {
      fill: "#333333", // צבע רקע כפתורים
      stroke: "#ffffff", // צבע גבול כפתורים
      style: {
        color: "#a1a1a1", // צבע טקסט בכפתורים
      },
      states: {
        hover: {
          fill: "#555555", // צבע רקע בזמן ריחוף מעל כפתורים
          stroke: "#ffffff", // צבע גבול בזמן ריחוף מעל כפתורים
        },
        select: {
          fill: "#444444", // צבע רקע כאשר נבחר כפתור
          stroke: "#ffffff", // צבע גבול כאשר נבחר כפתור
        },
      },
    },
  },
  responsive: {
    rules: [
      {
        condition: {
          maxWidth: 800,
        },
        chartOptions: {
          rangeSelector: {
            inputEnabled: true,
          },
        },
      },
    ],
  },
  stockTools: {
    gui: {
      enabled: true, // Enable the GUI for stock tools
      icons: [
        "series-type",
        "indicators",
        "annotations",
        "zoom-in",
        "zoom-out",
        "pan",
        "reset",
      ],
    },
  },
});
