// src/App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "./index.css";
import "./App.css";
import MyNavbar from "./components/MyNavbar";
import AdvancedTradingViewChart from "./components/AdvancedTradingViewChart";
import TradeForm from "./components/TradeForm";
import Dashboard from "./components/Dashboard";
import LoginForm from "./components/LoginForm";
import RegisterForm from "./components/RegisterForm";

const App = () => {
  const [darkMode, setDarkMode] = useState(false);

  const handleToggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const currentSymbol = "NASDAQ:AAPL";
  const currentPrice = 150.0;

  return (
    <Router>
      <MyNavbar darkMode={darkMode} onToggleDarkMode={handleToggleDarkMode} />
      <Routes>
        <Route
          path="/stocks"
          element={
            <>
              <AdvancedTradingViewChart theme={darkMode ? "dark" : "light"} />
              <TradeForm
                currentSymbol={currentSymbol}
                currentPrice={currentPrice}
              />
              <Dashboard />
            </>
          }
        />
        <Route path="/login" element={<LoginForm />} />
        <Route path="/register" element={<RegisterForm />} />
      </Routes>
    </Router>
  );
};

export default App;
