import React, { useState } from "react";
import { Button, Form, ListGroup } from "react-bootstrap";
import Select from "react-select";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Watchlist.css";

const symbolOptions = [
  { value: "NASDAQ:AAPL", label: "Apple Inc." },
  { value: "NASDAQ:GOOGL", label: "Alphabet Inc." },
  { value: "NASDAQ:AMZN", label: "Amazon.com Inc." },
  // ניתן להוסיף עוד אופציות לפי הצורך
];

const Watchlist = ({ onSelectSymbol, isCollapsed }) => {
  const [watchlist, setWatchlist] = useState([]);
  const [selectedSymbol, setSelectedSymbol] = useState(null);

  const handleAddSymbol = () => {
    if (selectedSymbol && !watchlist.includes(selectedSymbol.value)) {
      setWatchlist([...watchlist, selectedSymbol.value]);
      setSelectedSymbol(null);
    }
  };

  const handleRemoveSymbol = (symbol) => {
    setWatchlist(watchlist.filter((item) => item !== symbol));
  };

  return (
    <div className="watchlist">
      <h5>Watchlist</h5>
      {!isCollapsed && (
        <>
          <Form
            onSubmit={(e) => {
              e.preventDefault();
              handleAddSymbol();
            }}
          >
            <Form.Group controlId="formNewSymbol">
              <Select
                value={selectedSymbol}
                onChange={setSelectedSymbol}
                options={symbolOptions}
                placeholder="Search for a symbol..."
                isClearable
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Add
            </Button>
          </Form>
          <ListGroup>
            {watchlist.map((symbol) => (
              <ListGroup.Item key={symbol}>
                <div className="watchlist-item">
                  <span onClick={() => onSelectSymbol(symbol)}>{symbol}</span>
                  <Button
                    variant="danger"
                    size="sm"
                    onClick={() => handleRemoveSymbol(symbol)}
                  >
                    Remove
                  </Button>
                </div>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </>
      )}
    </div>
  );
};

export default Watchlist;
