import React, { useState } from "react";
import { Navbar, Nav, Container, NavDropdown } from "react-bootstrap";
import "./MyNavbar.css";
import logoLight from "./path-to-light-logo.png"; // עדכן את הנתיב ללוגו במצב בהיר
import logoDark from "./path-to-dark-logo.png"; // עדכן את הנתיב ללוגו במצב כהה

const MyNavbar = ({ onToggleTheme }) => {
  const [darkMode, setDarkMode] = useState(false);

  const handleToggle = () => {
    const newTheme = !darkMode ? "dark" : "light";
    setDarkMode(!darkMode);
    onToggleTheme(newTheme);
  };

  return (
    <Navbar
      bg={darkMode ? "dark" : "light"}
      variant={darkMode ? "dark" : "light"}
      expand="lg"
    >
      <Container>
        <Navbar.Brand href="/home">MyStockTrader</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="/home">Home</Nav.Link>
            <Nav.Link href="/stocks">Stocks</Nav.Link>
            <NavDropdown title="More" id="basic-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Portfolio</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">Trade</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Reports</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">Settings</NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <img
            src={darkMode ? logoDark : logoLight}
            alt="Toggle Theme"
            style={{ cursor: "pointer", width: "40px", height: "40px" }}
            onClick={handleToggle}
          />
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default MyNavbar;
