import React, { useState } from "react";
import { Button, Form, Container, Row, Col } from "react-bootstrap";

const TradeForm = () => {
  const [action, setAction] = useState("buy");
  const [symbol, setSymbol] = useState("");
  const [quantity, setQuantity] = useState("");
  const [price, setPrice] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // כאן תוכל להוסיף לוגיקה לקנייה או מכירה של מניות
    alert(
      `Action: ${action}\nSymbol: ${symbol}\nQuantity: ${quantity}\nPrice: ${price}`
    );
  };

  return (
    <Container className="my-4">
      <Row>
        <Col>
          <h2>{action === "buy" ? "Buy Stock" : "Sell Stock"}</h2>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3" controlId="formSymbol">
              <Form.Label>Symbol</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter stock symbol"
                value={symbol}
                onChange={(e) => setSymbol(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formQuantity">
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter quantity"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                step="0.01"
                placeholder="Enter price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              />
            </Form.Group>
            <Button
              variant={action === "buy" ? "success" : "danger"}
              type="submit"
            >
              {action === "buy" ? "Buy" : "Sell"}
            </Button>
          </Form>
        </Col>
      </Row>
      <Row>
        <Col>
          <Button variant="primary" onClick={() => setAction("buy")}>
            Buy
          </Button>
          <Button
            variant="secondary"
            onClick={() => setAction("sell")}
            className="ms-2"
          >
            Sell
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default TradeForm;
