// BitcoinChart.js
import React, { useEffect, useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import "./BitcoinChart.css"; // נייבא את קובץ ה-CSS המתאים

const BitcoinChart = () => {
  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    const fetchBitcoinData = async () => {
      const data = await fetch(
        "https://demo-live-data.highcharts.com/aapl-ohlcv.json"
      ).then((response) => response.json());

      const ohlc = [],
        volume = [],
        dataLength = data.length;

      for (let i = 0; i < dataLength; i += 1) {
        ohlc.push([
          data[i][0], // the date
          data[i][1], // open
          data[i][2], // high
          data[i][3], // low
          data[i][4], // close
        ]);

        volume.push([
          data[i][0], // the date
          data[i][5], // the volume
        ]);
      }

      setChartOptions({
        chart: {
          backgroundColor: "#353535", // שחור רקע
          style: {
            fontFamily: "Arial, sans-serif",
          },
          events: {
            load: function () {
              addGUIEvents(this);
            },
          },
        },
        title: {
          text: "Bitcoin Price Chart",
          style: {
            color: "#ffffff", // לבן כותרת
          },
        },
        xAxis: {
          crosshair: {
            className: "highcharts-crosshair-custom",
            enabled: true,
          },
          labels: {
            style: {
              color: "#ffffff", // לבן תוויות
            },
          },
        },
        yAxis: [
          {
            title: {
              text: "Price (USD)",
              style: {
                color: "#ffffff", // לבן כותרת ציר
              },
            },
            crosshair: {
              snap: false,
              className: "highcharts-crosshair-custom",
              label: {
                className: "highcharts-crosshair-custom-label",
                enabled: true,
                format: "{value:.2f}",
                style: {
                  color: "#ffffff", // לבן תוויות ציר
                },
              },
              enabled: true,
            },
            labels: {
              style: {
                color: "#ffffff", // לבן תוויות
              },
            },
            height: "70%",
          },
          {
            title: {
              text: "Volume",
              style: {
                color: "#ffffff", // לבן כותרת ציר
              },
            },
            crosshair: {
              className: "highcharts-crosshair-custom",
              snap: false,
              enabled: true,
              label: {
                format:
                  "{#if value ge 1000000} {(divide value 1000000):.2f} M {else} {value} {/if}",
                className: "highcharts-crosshair-custom-label",
                enabled: true,
                style: {
                  color: "#ffffff", // לבן תוויות ציר
                },
              },
            },
            labels: {
              style: {
                color: "#ffffff", // לבן תוויות
              },
            },
            top: "70%",
            height: "30%",
            offset: 0,
          },
        ],
        tooltip: {
          shape: "square",
          split: false,
          shared: true,
          headerShape: "callout",
          shadow: false,
          backgroundColor: "#333333", // שחור רקע חלון מידע
          borderColor: "#ffffff", // לבן גבול חלון מידע
          style: {
            color: "#ffffff", // לבן טקסט חלון מידע
          },
        },
        series: [
          {
            type: "candlestick",
            id: "aapl-ohlc",
            name: "Bitcoin Price",
            upColor: "#00ff00", // ירוק עליות
            color: "#ff0000", // אדום ירידות
            data: ohlc,
          },
          {
            type: "column",
            id: "aapl-volume",
            name: "Volume",
            data: volume,
            yAxis: 1,
          },
        ],
        rangeSelector: {
          selected: 1, // Default to 3 months
          buttons: [
            {
              type: "month",
              count: 1,
              text: "1m",
            },
            {
              type: "month",
              count: 3,
              text: "3m",
            },
            {
              type: "month",
              count: 6,
              text: "6m",
            },
            {
              type: "ytd",
              text: "YTD",
            },
            {
              type: "year",
              count: 1,
              text: "1y",
            },
            {
              type: "all",
              text: "All",
            },
          ],
          buttonTheme: {
            fill: "#333333", // צבע רקע כפתורים
            stroke: "#ffffff", // צבע גבול כפתורים
            style: {
              color: "#a1a1a1", // צבע טקסט בכפתורים
            },
            states: {
              hover: {
                fill: "#555555", // צבע רקע בזמן ריחוף מעל כפתורים
                stroke: "#ffffff", // צבע גבול בזמן ריחוף מעל כפתורים
              },
              select: {
                fill: "#444444", // צבע רקע כאשר נבחר כפתור
                stroke: "#ffffff", // צבע גבול כאשר נבחר כפתור
              },
            },
          },
        },
        stockTools: {
          gui: {
            enabled: true,
            showOnMobile: true,
            buttons: [
              {
                name: "Add Indicator",
                iconClassName: "highcharts-icon-indicator",
                onclick: function () {
                  this.stockTools.gui.showPopup("indicators");
                },
              },
              {
                name: "Add Annotation",
                iconClassName: "highcharts-icon-annotation",
                onclick: function () {
                  this.stockTools.gui.showPopup("annotation-toolbar");
                },
              },
              {
                name: "Zoom In",
                iconClassName: "highcharts-icon-zoom-in",
                onclick: function () {
                  this.zoomIn();
                },
              },
              {
                name: "Zoom Out",
                iconClassName: "highcharts-icon-zoom-out",
                onclick: function () {
                  this.zoomOut();
                },
              },
              {
                name: "Pan",
                iconClassName: "highcharts-icon-pan",
                onclick: function () {
                  this.pan();
                },
              },
              {
                name: "Reset Zoom",
                iconClassName: "highcharts-icon-reset-zoom",
                onclick: function () {
                  this.resetZoom();
                },
              },
            ],
          },
        },
        responsive: {
          rules: [
            {
              condition: {
                maxWidth: 800,
              },
              chartOptions: {
                rangeSelector: {
                  inputEnabled: false,
                },
                stockTools: {
                  gui: {
                    showOnMobile: false,
                  },
                },
              },
            },
          ],
        },
      });
    };

    fetchBitcoinData();
  }, []);

  const addGUIEvents = (chart) => {
    const closePopupButtons = document.getElementsByClassName(
      "highcharts-close-popup"
    );

    // Close popup button:
    Highcharts.addEvent(closePopupButtons[0], "click", function () {
      this.parentNode.style.display = "none";
    });

    Highcharts.addEvent(closePopupButtons[1], "click", function () {
      this.parentNode.style.display = "none";
    });

    // Add an indicator from popup
    Highcharts.addEvent(
      document.querySelectorAll(".highcharts-popup-indicators button")[0],
      "click",
      function () {
        const typeSelect = document.querySelectorAll(
          ".highcharts-popup-indicators select"
        )[0];
        const type = typeSelect.options[typeSelect.selectedIndex].value;
        const period =
          document.querySelectorAll(".highcharts-popup-indicators input")[0]
            .value || 14;

        chart.addSeries({
          linkedTo: "aapl-ohlc",
          type: type,
          params: {
            period: parseInt(period, 10),
          },
        });

        chart.stockToolbar.indicatorsPopupContainer.style.display = "none";
      }
    );

    // Update an annotation from popup
    Highcharts.addEvent(
      document.querySelectorAll(".highcharts-popup-annotations button")[0],
      "click",
      function () {
        const strokeWidth = parseInt(
          document.querySelectorAll(
            '.highcharts-popup-annotations input[name="stroke-width"]'
          )[0].value,
          10
        );
        const strokeColor = document.querySelectorAll(
          '.highcharts-popup-annotations input[name="stroke"]'
        )[0].value;

        // Stock/advanced annotations have common options under typeOptions
        if (chart.currentAnnotation.options.typeOptions) {
          chart.currentAnnotation.update({
            typeOptions: {
              lineColor: strokeColor,
              lineWidth: strokeWidth,
              line: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              background: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              innerBackground: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              outerBackground: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              connector: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
            },
          });
        } else {
          // Basic annotations:
          chart.currentAnnotation.update({
            shapes: [
              {
                "stroke-width": strokeWidth,
                stroke: strokeColor,
              },
            ],
            labels: [
              {
                borderWidth: strokeWidth,
                borderColor: strokeColor,
              },
            ],
          });
        }
        chart.stockToolbar.annotationsPopupContainer.style.display = "none";
      }
    );
  };

  return (
    <div>
      <HighchartsReact
        highcharts={Highcharts}
        constructorType={"stockChart"}
        options={chartOptions}
      />
    </div>
  );
};

export default BitcoinChart;
