import React, { useState } from "react";

const TradeForm = ({ symbol, price }) => {
  const [quantity, setQuantity] = useState("");
  const [total, setTotal] = useState(0);

  const handleQuantityChange = (event) => {
    const newQuantity = event.target.value;
    setQuantity(newQuantity);
    setTotal(newQuantity * price); // Calculate total based on quantity and current price
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(
      `Order placed for ${quantity} shares of ${symbol} at $${price} per share.`
    );
    // Add logic to handle form submission
  };

  return (
    <div className="trade-form">
      <h2>Trade {symbol}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Quantity:</label>
          <input
            type="number"
            value={quantity}
            onChange={handleQuantityChange}
            min="1"
            step="1"
          />
        </div>
        <div className="form-group">
          <label>Price:</label>
          <input type="text" value={`$${price.toFixed(2)}`} readOnly />
        </div>
        <div className="form-group">
          <label>Total:</label>
          <input type="text" value={`$${total.toFixed(2)}`} readOnly />
        </div>
        <button type="submit">Place Order</button>
      </form>
    </div>
  );
};

export default TradeForm;
