// BitcoinChart.js
import React, { useEffect, useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import "./BitcoinChart.css"; // נייבא את קובץ ה-CSS המתאים

const BitcoinChart = () => {
  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    const fetchBitcoinData = async () => {
      const data = await fetch(
        "https://demo-live-data.highcharts.com/aapl-ohlcv.json"
      ).then((response) => response.json());

      const ohlc = [],
        volume = [],
        dataLength = data.length;

      for (let i = 0; i < dataLength; i += 1) {
        ohlc.push([
          data[i][0], // the date
          data[i][1], // open
          data[i][2], // high
          data[i][3], // low
          data[i][4], // close
        ]);

        volume.push([
          data[i][0], // the date
          data[i][5], // the volume
        ]);
      }

      setChartOptions({
        chart: {
          backgroundColor: "#353535", // שחור רקע
          height: 900, // גובה מוגדל של חלון הגרף
          style: {
            fontFamily: "Arial, sans-serif",
          },
        },
        title: {
          text: "Bitcoin Price Chart",
          style: {
            color: "#ffffff", // לבן כותרת
          },
        },
        xAxis: {
          gridLineWidth: 0, // הסתרת קווי הרשת בציר ה-X
          crosshair: {
            enabled: false, // הסתרת הקווים המאוזנים בציר ה-X
          },
          labels: {
            style: {
              color: "#ffffff", // לבן תוויות
            },
          },
        },
        yAxis: [
          {
            title: {
              text: "Price (USD)",
              style: {
                color: "#ffffff", // לבן כותרת ציר
              },
            },
            gridLineWidth: 0, // הסתרת קווי הרשת בציר ה-Y
            crosshair: {
              enabled: false, // הסתרת הקווים המאוזנים בציר ה-Y
            },
            labels: {
              style: {
                color: "#ffffff", // לבן תוויות
              },
            },
            height: "70%",
          },
          {
            title: {
              text: "Volume",
              style: {
                color: "#ffffff", // לבן כותרת ציר
              },
            },
            gridLineWidth: 0, // הסתרת קווי הרשת בציר ה-Y של ה-volume
            crosshair: {
              enabled: false, // הסתרת הקווים המאוזנים בציר ה-Y של ה-volume
            },
            labels: {
              style: {
                color: "#ffffff", // לבן תוויות
              },
            },
            top: "70%",
            height: "30%",
            offset: 0,
          },
        ],
        tooltip: {
          shape: "square",
          split: false,
          shared: true,
          headerShape: "callout",
          shadow: false,
          backgroundColor: "#333333", // שחור רקע חלון מידע
          borderColor: "#ffffff", // לבן גבול חלון מידע
          style: {
            color: "#ffffff", // לבן טקסט חלון מידע
          },
        },
        series: [
          {
            type: "candlestick",
            id: "aapl-ohlc",
            name: "AAPL Stock Price",
            upColor: "#00ff00", // ירוק עליות
            color: "#ff0000", // אדום ירידות
            data: ohlc,
          },
          {
            type: "column",
            id: "aapl-volume",
            name: "AAPL Volume",
            data: volume,
            yAxis: 1,
          },
        ],
        rangeSelector: {
          selected: 1, // Default to 3 months
          buttons: [
            {
              type: "month",
              count: 1,
              text: "1m",
            },
            {
              type: "month",
              count: 3,
              text: "3m",
            },
            {
              type: "month",
              count: 6,
              text: "6m",
            },
            {
              type: "ytd",
              text: "YTD",
            },
            {
              type: "year",
              count: 1,
              text: "1y",
            },
            {
              type: "all",
              text: "All",
            },
          ],
          buttonTheme: {
            fill: "#333333", // צבע רקע כפתורים
            stroke: "#ffffff", // צבע גבול כפתורים
            style: {
              color: "#a1a1a1", // צבע טקסט בכפתורים
              // fontWeight: "bold",
            },
            states: {
              hover: {
                fill: "#555555", // צבע רקע בזמן ריחוף מעל כפתורים
                stroke: "#ffffff", // צבע גבול בזמן ריחוף מעל כפתורים
              },
              select: {
                fill: "#444444", // צבע רקע כאשר נבחר כפתור
                stroke: "#ffffff", // צבע גבול כאשר נבחר כפתור
              },
            },
          },
        },
        responsive: {
          rules: [
            {
              condition: {
                maxWidth: 800,
              },
              chartOptions: {
                rangeSelector: {
                  inputEnabled: true,
                },
              },
            },
          ],
        },
      });
    };

    fetchBitcoinData();
  }, []);

  return (
    <div>
      <HighchartsReact
        highcharts={Highcharts}
        constructorType={"stockChart"}
        options={chartOptions}
      />
    </div>
  );
};

export default BitcoinChart;
