import React, { useEffect } from "react";

const AdvancedTradingViewChart = ({ onSymbolChange, onPriceChange }) => {
  useEffect(() => {
    // Create TradingView widget
    const widget = new window.TradingView.widget({
      autosize: true,
      symbol: "NASDAQ:AAPL",
      interval: "D",
      timezone: "Etc/UTC",
      theme: "light",
      style: "1",
      locale: "en",
      toolbar_bg: "#f1f3f6",
      enable_publishing: false,
      allow_symbol_change: true,
      container_id: "tradingview_advanced_chart",
      studies: ["MAExp@tv-basicstudies", "RSI@tv-basicstudies"],
      withdateranges: true,
      hide_side_toolbar: false,
      hide_top_toolbar: false,
      save_image: false,
      studies_overrides: {},
      overrides: {},
      // Update symbol and price when the symbol changes
      onSymbolChange: (symbolInfo) => {
        const { ticker } = symbolInfo;
        onSymbolChange(ticker); // Update the current symbol
        fetch(`https://api.tradingview.com/symbols/${ticker}/price`)
          .then((response) => response.json())
          .then((data) => onPriceChange(data.price)); // Update the current price
      },
      // Handle the event when a new price comes in
      onPriceChange: (price) => {
        onPriceChange(price);
      },
    });

    // Clean up the widget on component unmount
    return () => {
      if (widget) {
        widget.remove();
      }
    };
  }, [onSymbolChange, onPriceChange]);

  return (
    <div className="tradingview-widget-container">
      <div id="tradingview_advanced_chart" style={{ height: "800px" }}></div>
    </div>
  );
};

export default AdvancedTradingViewChart;
