import React from "react";
import { Navbar, Nav, NavDropdown, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./MyNavbar.css"; // הקובץ CSS עם ה-CSS העדכני

const MyNavbar = ({ theme, toggleTheme }) => {
  const isDarkMode = theme === "dark";

  return (
    <Navbar
      bg={isDarkMode ? "dark" : "light"}
      variant={isDarkMode ? "dark" : "light"}
      expand="lg"
    >
      <Container>
        <Navbar.Brand href="/home">MyStockTrader</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="/home">Home</Nav.Link>
            <Nav.Link href="/stocks">Stocks</Nav.Link>
            <NavDropdown title="More" id="basic-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Portfolio</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">Trade</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Reports</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">Settings</NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <div className="theme-toggle-container" onClick={toggleTheme}>
            <input
              type="checkbox"
              className="theme-toggle-checkbox"
              id="theme-toggle-checkbox"
              checked={isDarkMode}
              onChange={toggleTheme}
            />
            <label
              className="theme-toggle-label"
              htmlFor="theme-toggle-checkbox"
            >
              <span className="theme-toggle-switch">
                {isDarkMode ? "O" : "I"}
              </span>
            </label>
            <span className="theme-toggle-text">Dark Mode</span>
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default MyNavbar;
