// BitcoinChart.js
import React, { useEffect, useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import "./BitcoinChart.css"; // נייבא את קובץ ה-CSS המתאים

const BitcoinChart = () => {
  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    const fetchBitcoinData = async () => {
      const data = await fetch(
        "https://demo-live-data.highcharts.com/aapl-ohlcv.json"
      ).then((response) => response.json());

      const ohlc = [],
        volume = [],
        dataLength = data.length;

      for (let i = 0; i < dataLength; i += 1) {
        ohlc.push([
          data[i][0], // the date
          data[i][1], // open
          data[i][2], // high
          data[i][3], // low
          data[i][4], // close
        ]);

        volume.push([
          data[i][0], // the date
          data[i][5], // the volume
        ]);
      }

      setChartOptions({
        chart: {
          backgroundColor: "#353535", // צבע רקע כהה
          style: {
            fontFamily: "Arial, sans-serif",
          },
          events: {
            load: function () {
              addGUIEvents(this);
            },
          },
        },
        title: {
          text: "Bitcoin Price Chart",
          style: {
            color: "#ffffff", // צבע טקסט כותרת לבן
          },
        },
        xAxis: {
          crosshair: {
            className: "highcharts-crosshair-custom",
            enabled: true,
          },
          labels: {
            style: {
              color: "#ffffff", // צבע תוויות לבן
            },
          },
          gridLineWidth: 0, // הסרת קווי הרשת האנכיים
        },
        yAxis: [
          {
            title: {
              text: "Price (USD)",
              style: {
                color: "#ffffff", // צבע טקסט כותרת ציר ימין לבן
              },
            },
            crosshair: {
              snap: false,
              className: "highcharts-crosshair-custom",
              label: {
                className: "highcharts-crosshair-custom-label",
                enabled: true,
                format: "{value:.2f}",
                style: {
                  color: "#ffffff", // צבע טקסט של תוויות הציר לבן
                },
              },
              enabled: true,
            },
            labels: {
              style: {
                color: "#ffffff", // צבע תוויות ציר ימין לבן
              },
            },
            height: "70%",
            gridLineWidth: 0, // הסרת קווי הרשת האופקיים
          },
          {
            title: {
              text: "Volume",
              style: {
                color: "#ffffff", // צבע טקסט כותרת ציר ימין לבן
              },
            },
            crosshair: {
              className: "highcharts-crosshair-custom",
              snap: false,
              enabled: true,
              label: {
                format:
                  "{#if value ge 1000000} {(divide value 1000000):.2f} M {else} {value} {/if}",
                className: "highcharts-crosshair-custom-label",
                enabled: true,
                style: {
                  color: "#ffffff", // צבע טקסט של תוויות הציר לבן
                },
              },
            },
            labels: {
              style: {
                color: "#ffffff", // צבע תוויות ציר ימין לבן
              },
            },
            top: "70%",
            height: "30%",
            offset: 0,
          },
        ],
        tooltip: {
          shape: "square",
          split: false,
          shared: true,
          headerShape: "callout",
          shadow: false,
          backgroundColor: "#333333", // צבע רקע חלון מידע כהה
          borderColor: "#ffffff", // צבע גבול חלון מידע לבן
          style: {
            color: "#ffffff", // צבע טקסט חלון מידע לבן
          },
        },
        series: [
          {
            type: "candlestick",
            id: "aapl-ohlc",
            name: "Bitcoin Price",
            upColor: "#00ff00", // ירוק עבור עליות
            color: "#ff0000", // אדום עבור ירידות
            data: ohlc,
          },
          {
            type: "column",
            id: "aapl-volume",
            name: "Volume",
            data: volume,
            yAxis: 1,
          },
        ],
        rangeSelector: {
          selected: 1, // ברירת מחדל ל-3 חודשים
          buttons: [
            {
              type: "month",
              count: 1,
              text: "1m",
            },
            {
              type: "month",
              count: 3,
              text: "3m",
            },
            {
              type: "month",
              count: 6,
              text: "6m",
            },
            {
              type: "ytd",
              text: "YTD",
            },
            {
              type: "all",
              text: "All",
            },
          ],
        },
        stockTools: {
          gui: {
            enabled: true,
            icons: {
              indicator: "highcharts-icon-indicator",
              annotation: "highcharts-icon-annotation",
              zoomIn: "highcharts-icon-zoom-in",
              zoomOut: "highcharts-icon-zoom-out",
              pan: "highcharts-icon-pan",
              resetZoom: "highcharts-icon-reset-zoom",
            },
          },
          buttons: {
            indicators: {
              text: "Indicators",
            },
            annotations: {
              text: "Annotations",
            },
          },
        },
        responsive: {
          rules: [
            {
              condition: {
                maxWidth: 800,
              },
              chartOptions: {
                rangeSelector: {
                  inputEnabled: false,
                },
              },
            },
          ],
        },
      });
    };

    fetchBitcoinData();
  }, []);

  function addGUIEvents(chart) {
    Highcharts.addEvent(
      document.querySelectorAll(".highcharts-close-popup")[0],
      "click",
      function () {
        this.parentNode.style.display = "none";
      }
    );

    Highcharts.addEvent(
      document.querySelectorAll(".highcharts-popup-indicators button")[0],
      "click",
      function () {
        const typeSelect = document.querySelectorAll(
            ".highcharts-popup-indicators select"
          )[0],
          type = typeSelect.options[typeSelect.selectedIndex].value,
          period =
            document.querySelectorAll(".highcharts-popup-indicators input")[0]
              .value || 14;

        chart.addSeries({
          linkedTo: "aapl-ohlc",
          type: type,
          params: {
            period: parseInt(period, 10),
          },
        });

        chart.stockToolbar.indicatorsPopupContainer.style.display = "none";
      }
    );

    Highcharts.addEvent(
      document.querySelectorAll(".highcharts-popup-annotations " + "button")[0],
      "click",
      function () {
        const strokeWidth = parseInt(
            document.querySelectorAll(
              ".highcharts-popup-annotations " + 'input[name="stroke-width"]'
            )[0].value,
            10
          ),
          strokeColor = document.querySelectorAll(
            '.highcharts-popup-annotations input[name="stroke"]'
          )[0].value;

        if (chart.currentAnnotation.options.typeOptions) {
          chart.currentAnnotation.update({
            typeOptions: {
              lineColor: strokeColor,
              lineWidth: strokeWidth,
              line: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              background: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              innerBackground: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              outerBackground: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              connector: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
            },
          });
        } else {
          chart.currentAnnotation.update({
            shapes: [
              {
                "stroke-width": strokeWidth,
                stroke: strokeColor,
              },
            ],
            labels: [
              {
                borderWidth: strokeWidth,
                borderColor: strokeColor,
              },
            ],
          });
        }
        chart.stockToolbar.annotationsPopupContainer.style.display = "none";
      }
    );
  }

  return (
    <div className="chart-container">
      <HighchartsReact
        highcharts={Highcharts}
        constructorType={"stockChart"}
        options={chartOptions}
      />
    </div>
  );
};

export default BitcoinChart;
