// BitcoinChart.js
import React, { useEffect, useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";

const BitcoinChart = () => {
  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    const fetchBitcoinData = async () => {
      const data = await fetch(
        "https://demo-live-data.highcharts.com/aapl-ohlcv.json"
      ).then((response) => response.json());

      const ohlc = [],
        volume = [],
        dataLength = data.length;

      for (let i = 0; i < dataLength; i += 1) {
        ohlc.push([
          data[i][0], // the date
          data[i][1], // open
          data[i][2], // high
          data[i][3], // low
          data[i][4], // close
        ]);

        volume.push([
          data[i][0], // the date
          data[i][5],
          data[i][1] < data[i][4]
            ? "highcharts-point-up"
            : "highcharts-point-down", // the volume
        ]);
      }

      setChartOptions({
        chart: {
          backgroundColor: "#000000", // שחור רקע
          height: 600, // גובה מוגדל של חלון הגרף
          style: {
            fontFamily: "Arial, sans-serif",
          },
        },
        title: {
          text: "Bitcoin Price Chart",
          style: {
            color: "#ffffff", // לבן כותרת
          },
        },
        xAxis: {
          crosshair: {
            className: "highcharts-crosshair-custom",
            enabled: true,
          },
          labels: {
            style: {
              color: "#ffffff", // לבן תוויות
            },
          },
        },
        yAxis: [
          {
            title: {
              text: "Price (USD)",
              style: {
                color: "#ffffff", // לבן כותרת ציר
              },
            },
            crosshair: {
              snap: false,
              className: "highcharts-crosshair-custom",
              label: {
                className: "highcharts-crosshair-custom-label",
                enabled: true,
                format: "{value:.2f}",
              },
              enabled: true,
            },
            labels: {
              style: {
                color: "#ffffff", // לבן תוויות
              },
            },
            height: "70%",
          },
          {
            title: {
              text: "Volume",
              style: {
                color: "#ffffff", // לבן כותרת ציר
              },
            },
            crosshair: {
              className: "highcharts-crosshair-custom",
              snap: false,
              enabled: true,
              label: {
                format:
                  "{#if value ge 1000000} {(divide value 1000000):.2f} M {else} {value} {/if}",
                className: "highcharts-crosshair-custom-label",
                enabled: true,
              },
            },
            labels: {
              style: {
                color: "#ffffff", // לבן תוויות
              },
            },
            top: "70%",
            height: "30%",
            offset: 0,
          },
        ],
        tooltip: {
          shape: "square",
          split: false,
          shared: true,
          headerShape: "callout",
          shadow: false,
          backgroundColor: "#333333", // שחור רקע חלון מידע
          borderColor: "#ffffff", // לבן גבול חלון מידע
          style: {
            color: "#ffffff", // לבן טקסט חלון מידע
          },
        },
        series: [
          {
            type: "candlestick",
            id: "aapl-ohlc",
            name: "AAPL Stock Price",
            upColor: "#00ff00", // ירוק עליות
            color: "#ff0000", // אדום ירידות
            data: ohlc,
          },
          {
            type: "column",
            keys: ["x", "y", "className"],
            id: "aapl-volume",
            name: "AAPL Volume",
            data: volume,
            yAxis: 1,
          },
        ],
        rangeSelector: {
          verticalAlign: "bottom",
          inputStyle: {
            color: "#ffffff", // לבן טקסט קלט
            backgroundColor: "#333333", // שחור רקע קלט
          },
        },
        responsive: {
          rules: [
            {
              condition: {
                maxWidth: 800,
              },
              chartOptions: {
                rangeSelector: {
                  inputEnabled: false,
                },
              },
            },
          ],
        },
      });
    };

    fetchBitcoinData();
  }, []);

  return (
    <div>
      <HighchartsReact
        highcharts={Highcharts}
        constructorType={"stockChart"}
        options={chartOptions}
      />
    </div>
  );
};

export default BitcoinChart;

