// src/components/ChartToolbar.js
import React from "react";

const ChartToolbar = ({
  onChartTypeChange,
  onShowIndicators,
  onHideIndicators,
}) => {
  return (
    <div className="chart-toolbar">
      <button onClick={() => onChartTypeChange("candlestick")}>
        Candlestick
      </button>
      <button onClick={() => onChartTypeChange("ohlc")}>OHLC</button>
      <button onClick={onShowIndicators}>Show Indicators</button>
      <button onClick={onHideIndicators}>Hide Indicators</button>
    </div>
  );
};

export default ChartToolbar;
