// BitcoinChart.js
import React, { useEffect, useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import "./BitcoinChart.css"; // נייבא את קובץ ה-CSS המתאים

const BitcoinChart = () => {
  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    const fetchBitcoinData = async () => {
      const data = await fetch(
        "https://demo-live-data.highcharts.com/aapl-ohlcv.json"
      ).then((response) => response.json());

      const ohlc = [],
        volume = [],
        dataLength = data.length;

      for (let i = 0; i < dataLength; i += 1) {
        ohlc.push([
          data[i][0], // the date
          data[i][1], // open
          data[i][2], // high
          data[i][3], // low
          data[i][4], // close
        ]);

        volume.push([
          data[i][0], // the date
          data[i][5], // the volume
        ]);
      }

      setChartOptions({
        chart: {
          backgroundColor: "#222222", // שחור רקע
          height: 600, // גובה מוגדל של חלון הגרף
          style: {
            fontFamily: "Arial, sans-serif",
          },
          events: {
            load: function () {
              addPopupEvents(this);
            },
          },
        },
        title: {
          text: "Bitcoin Price Chart",
          style: {
            color: "#ffffff", // לבן כותרת
          },
        },
        xAxis: {
          crosshair: {
            className: "highcharts-crosshair-custom",
            enabled: true,
          },
          labels: {
            style: {
              color: "#ffffff", // לבן תוויות
            },
          },
        },
        yAxis: [
          {
            title: {
              text: "Price (USD)",
              style: {
                color: "#ffffff", // לבן כותרת ציר
              },
            },
            crosshair: {
              snap: false,
              className: "highcharts-crosshair-custom",
              label: {
                className: "highcharts-crosshair-custom-label",
                enabled: true,
                format: "{value:.2f}",
                style: {
                  color: "#ffffff", // לבן תוויות ציר
                },
              },
              enabled: true,
            },
            labels: {
              style: {
                color: "#ffffff", // לבן תוויות
              },
            },
            height: "70%",
          },
          {
            title: {
              text: "Volume",
              style: {
                color: "#ffffff", // לבן כותרת ציר
              },
            },
            crosshair: {
              className: "highcharts-crosshair-custom",
              snap: false,
              enabled: true,
              label: {
                format:
                  "{#if value ge 1000000} {(divide value 1000000):.2f} M {else} {value} {/if}",
                className: "highcharts-crosshair-custom-label",
                enabled: true,
                style: {
                  color: "#ffffff", // לבן תוויות ציר
                },
              },
            },
            labels: {
              style: {
                color: "#ffffff", // לבן תוויות
              },
            },
            top: "70%",
            height: "30%",
            offset: 0,
          },
        ],
        tooltip: {
          shape: "square",
          split: false,
          shared: true,
          headerShape: "callout",
          shadow: false,
          backgroundColor: "#333333", // שחור רקע חלון מידע
          borderColor: "#ffffff", // לבן גבול חלון מידע
          style: {
            color: "#ffffff", // לבן טקסט חלון מידע
          },
        },
        series: [
          {
            type: "candlestick",
            id: "aapl-ohlc",
            name: "AAPL Stock Price",
            upColor: "#00ff00", // ירוק עליות
            color: "#ff0000", // אדום ירידות
            data: ohlc,
          },
          {
            type: "column",
            id: "aapl-volume",
            name: "AAPL Volume",
            data: volume,
            yAxis: 1,
          },
        ],
        navigationBindings: {
          events: {
            selectButton: function (event) {
              let newClassName =
                event.button.className + " " + "highcharts-active";
              const topButton = event.button.parentNode.parentNode;

              if (topButton.classList.contains("right")) {
                newClassName += " right";
              }

              // If this is a button with sub buttons,
              // change main icon to the current one:
              if (!topButton.classList.contains("highcharts-menu-wrapper")) {
                topButton.className = newClassName;
              }

              // Store info about active button:
              this.chart.activeButton = event.button;
            },
            deselectButton: function (event) {
              event.button.parentNode.parentNode.classList.remove(
                "highcharts-active"
              );

              // Remove info about active button:
              this.chart.activeButton = null;
            },
            showPopup: function (event) {
              if (!this.indicatorsPopupContainer) {
                this.indicatorsPopupContainer = document.getElementsByClassName(
                  "highcharts-popup-indicators"
                )[0];
              }

              if (!this.annotationsPopupContainer) {
                this.annotationsPopupContainer =
                  document.getElementsByClassName(
                    "highcharts-popup-annotations"
                  )[0];
              }

              if (event.formType === "indicators") {
                this.indicatorsPopupContainer.style.display = "block";
              } else if (event.formType === "annotation-toolbar") {
                // If user is still adding an annotation, don't show
                // popup:
                if (!this.chart.activeButton) {
                  this.chart.currentAnnotation = event.annotation;
                  this.annotationsPopupContainer.style.display = "block";
                }
              }
            },
            closePopup: function () {
              this.indicatorsPopupContainer.style.display = "none";
              this.annotationsPopupContainer.style.display = "none";
            },
          },
        },
        stockTools: {
          gui: {
            enabled: false,
          },
        },
        responsive: {
          rules: [
            {
              condition: {
                maxWidth: 800,
              },
              chartOptions: {
                rangeSelector: {
                  inputEnabled: false,
                },
              },
            },
          ],
        },
      });
    };

    fetchBitcoinData();
  }, []);

  function addPopupEvents(chart) {
    const closePopupButtons = document.getElementsByClassName(
      "highcharts-close-popup"
    );
    // Close popup button:
    Highcharts.addEvent(closePopupButtons[0], "click", function () {
      this.parentNode.style.display = "none";
    });

    Highcharts.addEvent(closePopupButtons[1], "click", function () {
      this.parentNode.style.display = "none";
    });

    // Add an indicator from popup
    Highcharts.addEvent(
      document.querySelectorAll(".highcharts-popup-indicators button")[0],
      "click",
      function () {
        const typeSelect = document.querySelectorAll(
            ".highcharts-popup-indicators select"
          )[0],
          type = typeSelect.options[typeSelect.selectedIndex].value,
          period =
            document.querySelectorAll(".highcharts-popup-indicators input")[0]
              .value || 14;

        chart.addSeries({
          linkedTo: "aapl-ohlc",
          type: type,
          params: {
            period: parseInt(period, 10),
          },
        });

        chart.stockToolbar.indicatorsPopupContainer.style.display = "none";
      }
    );

    // Update an annotaiton from popup
    Highcharts.addEvent(
      document.querySelectorAll(".highcharts-popup-annotations " + "button")[0],
      "click",
      function () {
        const strokeWidth = parseInt(
            document.querySelectorAll(
              ".highcharts-popup-annotations " + 'input[name="stroke-width"]'
            )[0].value,
            10
          ),
          strokeColor = document.querySelectorAll(
            '.highcharts-popup-annotations input[name="stroke"]'
          )[0].value;

        // Stock/advanced annotations have common options under
        // typeOptions
        if (chart.currentAnnotation.options.typeOptions) {
          chart.currentAnnotation.update({
            typeOptions: {
              lineColor: strokeColor,
              lineWidth: strokeWidth,
              line: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              background: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              innerBackground: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              outerBackground: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
              connector: {
                strokeWidth: strokeWidth,
                stroke: strokeColor,
              },
            },
          });
        } else {
          // Basic annotations:
          chart.currentAnnotation.update({
            shapes: [
              {
                "stroke-width": strokeWidth,
                stroke: strokeColor,
              },
            ],
            labels: [
              {
                borderWidth: strokeWidth,
                borderColor: strokeColor,
              },
            ],
          });
        }
        chart.stockToolbar.annotationsPopupContainer.style.display = "none";
      }
    );
  }

  return (
    <div>
      <HighchartsReact
        highcharts={Highcharts}
        constructorType={"stockChart"}
        options={chartOptions}
      />
      <div className="highcharts-popup-indicators" style={{ display: "none" }}>
        <span className="highcharts-close-popup">x</span>
        <h3>Add indicator</h3>
        <select>
          <option value="sma">SMA</option>
          <option value="ema">EMA</option>
        </select>
        <input type="number" name="period" placeholder="Period (default: 14)" />
        <button>Add</button>
      </div>
      <div className="highcharts-popup-annotations" style={{ display: "none" }}>
        <span className="highcharts-close-popup">x</span>
        <h3>Update annotation</h3>
        <input type="color" name="stroke" placeholder="Color" />
        <input type="number" name="stroke-width" placeholder="Width" />
        <button>Update</button>
      </div>
    </div>
  );
};

export default BitcoinChart;
