// src/components/TradeForm.js
import React, { useState, useEffect } from "react";
import "./TradeForm.css";

const TradeForm = ({ currentSymbol, currentPrice }) => {
  const [quantity, setQuantity] = useState(0);
  const [action, setAction] = useState("buy");
  const [totalAmount, setTotalAmount] = useState(0);
  const [showConfirmation, setShowConfirmation] = useState(false);

  useEffect(() => {
    setTotalAmount(quantity * currentPrice);
  }, [quantity, currentPrice]);

  const handleSubmit = (event) => {
    event.preventDefault();
    // כאן ניתן להוסיף את הקוד לשליחת פקודת קנייה או מכירה לשרת
    console.log(
      `Symbol: ${currentSymbol}, Action: ${action}, Quantity: ${quantity}, Total Amount: ${totalAmount}`
    );
    setShowConfirmation(true);
  };

  return (
    <div className="trade-form-container">
      <div className={`trade-form ${action}`}>
        <h2>{action === "buy" ? "Buy Order" : "Sell Order"}</h2>
        <form onSubmit={handleSubmit}>
          <div className="trade-form-row">
            <label>Symbol:</label>
            <span>{currentSymbol}</span>
          </div>
          <div className="trade-form-row">
            <label>Price:</label>
            <span>${currentPrice.toFixed(2)}</span>
          </div>
          <div className="trade-form-row">
            <label>Quantity:</label>
            <input
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              min="0.01"
              step="0.01"
              required
            />
          </div>
          <div className="trade-form-row">
            <label>Action:</label>
            <select value={action} onChange={(e) => setAction(e.target.value)}>
              <option value="buy">Buy</option>
              <option value="sell">Sell</option>
            </select>
          </div>
          <div className="trade-form-row">
            <label>Total Amount:</label>
            <span>${totalAmount.toFixed(2)}</span>
          </div>
          <button
            type="submit"
            className={`trade-form-submit-button ${action}`}
          >
            {action === "buy" ? "Buy Now" : "Sell Now"}
          </button>
        </form>
        {showConfirmation && (
          <div className="trade-form-confirmation">
            <p>Order Submitted!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default TradeForm;
