// src/components/BitcoinChart.js
import React, { useEffect, useRef, useState } from "react";
import Highcharts from "highcharts/highstock";
import HighchartsReact from "highcharts-react-official";
import { getChartOptions } from "./chartOptions";
import "./BitcoinChart.css";
import ChartToolbar from "./ChartToolbar";

const BitcoinChart = () => {
  const chartComponent = useRef(null);
  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    fetch("https://demo-live-data.highcharts.com/aapl-ohlcv.json")
      .then((response) => response.json())
      .then((data) => {
        const ohlcData = [];
        const volumeData = [];

        data.forEach((item) => {
          ohlcData.push([item[0], item[1], item[2], item[3], item[4]]);
          volumeData.push([item[0], item[5]]);
        });

        setChartOptions(getChartOptions(ohlcData, volumeData));
      });
  }, []);

  const handleChartTypeChange = (type) => {
    const newOptions = { ...chartOptions };
    newOptions.series[0].type = type;
    setChartOptions(newOptions);
  };

  const handleShowIndicators = () => {
    const newOptions = { ...chartOptions };
    // הוספת קוד להצגת אינדיקטורים
    setChartOptions(newOptions);
  };

  const handleHideIndicators = () => {
    const newOptions = { ...chartOptions };
    // הוספת קוד להסתרת אינדיקטורים
    setChartOptions(newOptions);
  };

  return (
    <div className="chart-container">
      <ChartToolbar
        onChartTypeChange={handleChartTypeChange}
        onShowIndicators={handleShowIndicators}
        onHideIndicators={handleHideIndicators}
      />
      <HighchartsReact
        highcharts={Highcharts}
        constructorType={"stockChart"}
        options={chartOptions}
        ref={chartComponent}
      />
    </div>
  );
};

export default BitcoinChart;
