import React, { useEffect, useState } from "react";

const AdvancedTradingViewChart = ({ onSymbolChange, onPriceChange }) => {
  const [widget, setWidget] = useState(null);

  useEffect(() => {
    const newWidget = new window.TradingView.widget({
      autosize: true,
      symbol: "NASDAQ:AAPL",
      interval: "D",
      timezone: "Etc/UTC",
      theme: "light",
      style: "1",
      locale: "en",
      toolbar_bg: "#f1f3f6",
      enable_publishing: false,
      allow_symbol_change: true,
      container_id: "tradingview_advanced_chart",
      studies: ["MAExp@tv-basicstudies", "RSI@tv-basicstudies"],
      withdateranges: true,
      hide_side_toolbar: false,
      hide_top_toolbar: false,
      save_image: false,
      studies_overrides: {},
      overrides: {},
      // Event listener to capture symbol change
      onSymbolChange: (symbolInfo) => {
        const { ticker, description, price } = symbolInfo;
        onSymbolChange(ticker);
        onPriceChange(price);
      },
    });

    setWidget(newWidget);

    return () => {
      newWidget.remove();
    };
  }, [onSymbolChange, onPriceChange]);

  return (
    <div className="tradingview-widget-container">
      <div id="tradingview_advanced_chart" style={{ height: "800px" }}></div>
    </div>
  );
};

export default AdvancedTradingViewChart;
